import React, {Component} from 'react';
import {Task} from "./Task";

export class Uncompleted extends Component {

    constructor(props) {
        super(props);
        this.state = {
            tasks: [
                {todo: 'buy cat'}
            ]
        }
    }

    displayTasks = (tasks) => {
            let output = []
        tasks.forEach(task => {
        output.push(<Task task={task} />);
        })
        return output;
    }

    render () {
        return (
            <ul className='uncomplited'>
                {this.displayTasks(this.state.tasks)}
            </ul>
        )
    }
}
