import React, {Component} from 'react';
import {Uncompleted} from "../Components/Uncompleted";

export class Homepage extends Component {

    constructor(props) {
        super(props);
        this.getToDos();
    }

    getToDos = () => {
        fetch('http://localhost:3000/tasks')
            .then(response => response.json())
            .then((data) => {
                console.log(data)
            })
    }
    render () {
        return (
            <div>
                <h1>Uncompleted tasks</h1>
                <Uncompleted />
            </div>
        )
    }
}
